{
  "build": {
    "beforeBuildCommand": "",
    "beforeDevCommand": "",
    "frontendDist": "../gui",
    "devUrl": "http://127.0.0.1:8000/menu.html"
  },
  "bundle": {
    "active": true,
    "targets": "all",
    "createUpdaterArtifacts": true,
    "resources": [
      "bin/node-darwin-arm64",
      "../backend/config/agents.toml"
    ],
    "icon": [
      "icons/32x32.png",
      "icons/128x128.png",
      "icons/128x128@2x.png",
      "icons/icon.icns",
      "icons/icon.ico"
    ],
    "macOS": {
      "dmg": {
        "background": "./icons/dmg-background.png",
        "windowSize": {
          "width": 540,
          "height": 380
        },
        "appPosition": {
          "x": 390,
          "y": 170
        },
        "applicationFolderPosition": {
          "x": 150,
          "y": 170
        }
      }
    }
  },
  "productName": "Phantom",
  "mainBinaryName": "Phantom",
  "version": "1.0.3",
  "identifier": "com.phantom.app",
  "plugins": {
    "updater": {
      "endpoints": [
        "https://github.com/zruss11/Phantom/releases/latest/download/latest.json"
      ],
      "pubkey": "REDACTED"
    }
  },
  "app": {
    "withGlobalTauri": true,
    "macOSPrivateApi": true,
    "security": {
      "freezePrototype": true,
      "csp": "default-src 'self'; script-src 'self' 'unsafe-inline' https://code.iconify.design https://cdn.jsdelivr.net; style-src 'self' 'unsafe-inline'; connect-src ipc: http://ipc.localhost 'self' https://api.openai.com https://api.anthropic.com https://api.iconify.design https://api.simplesvg.com https://api.unisvg.com; img-src 'self' data: blob: https:; font-src 'self' data:"
    },
    "windows": [
      {
        "title": "Phantom",
        "width": 1220,
        "height": 860,
        "resizable": false,
        "decorations": false,
        "transparent": true,
        "url": "menu.html",
        "devtools": true,
        "useHttpsScheme": true
      }
    ]
  }
}

